// frontend/src/profileedit/UserProfileEdit.js

import React, { useState, useEffect } from "react";
import axios from "axios";
import "./UserProfileEdit.css";

function UserProfileEdit() {
  const [userData, setUserData] = useState({});
  const [updatedUserData, setUpdatedUserData] = useState({});

  useEffect(() => {
    // API 요청을 통해 사용자 정보를 가져오는 함수
    axios
      .get("/api/user-profile/<user_id>/")
      .then((response) => {
        setUserData(response.data);
        setUpdatedUserData(response.data); // 사용자 정보 초기화
      })
      .catch((error) => console.error(error));
  }, []);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setUpdatedUserData({ ...updatedUserData, [name]: value });
  };

  const handleFormSubmit = () => {
    // API를 통해 수정된 사용자 정보를 업데이트하는 함수
    
    axios
      .put(`/api/user-profile/${user_id}/`, updatedUserData)
      .then((response) => {
        setUserData(response.data);
      })
      .catch((error) => console.error(error));
  };

  return (
    <div>
      <h1>User Profile Edit</h1>
      <form>
        <label htmlFor="username">Username:</label>
        <input
          type="text"
          id="username"
          name="username"
          value={updatedUserData.username}
          onChange={handleInputChange}
        />
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          name="email"
          value={updatedUserData.email}
          onChange={handleInputChange}
        />
        <button type="button" onClick={handleFormSubmit}>
          Update Profile
        </button>
      </form>
    </div>
  );
}

export default UserProfileEdit;
