// frontend/src/components/ProfileEdit.js

import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

function ProfileEdit() {
  const { userId } = useParams(); // 동적으로 추출한 사용자 ID
  const [userData, setUserData] = useState({
    username: "",
    email: "",
  });
  const [updatedUserData, setUpdatedUserData] = useState({
    username: "",
    email: "",
  });

  useEffect(() => {
    // API를 통해 사용자 데이터를 불러와 초기화
    axios.get(`/api/user-profile/${userId}/`).then((response) => {
      setUserData(response.data);
      setUpdatedUserData(response.data);
    });
  }, [userId]);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setUpdatedUserData({ ...updatedUserData, [name]: value });
  };

  const handleFormSubmit = () => {
    // 수정된 사용자 데이터를 서버로 보내고 업데이트
    axios
      .put(`/api/user-profile/${userId}/`, updatedUserData)
      .then((response) => {
        setUserData(response.data);
      })
      .catch((error) => console.error(error));
  };

  return (
    <div>
      <h1>Profile Edit</h1>
      <form>
        <div>
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            name="username"
            value={updatedUserData.username}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={updatedUserData.email}
            onChange={handleInputChange}
          />
        </div>
        <button type="button" onClick={handleFormSubmit}>
          Update Profile
        </button>
      </form>
    </div>
  );
}

export default ProfileEdit;
